var getStatus = function(i, history, status)
{
    if (status != 'INITIALIZED')
        return status;

	if ((i+1) >= history.getRowCount())
        return status;

    var nextRow = history.getRow(i+1);
    if (nextRow.watcherStatus == 'CALIBRATING')
        return nextRow.watcherStatus;
    else
	    return status;
};

