    alertStatusHistory = Resources["InfoTableFunctions"].Sort({
        t:          alertStatusHistory,
        ascending:  false,
        sortColumn: 'timestamp'
    });

    var totalRows = alertStatusHistory.getRowCount();
    for (var i=0; i < totalRows; i++)
    {
        var row = alertStatusHistory.getRow(i);
        var nextRow;
        if ((i+1) < totalRows) nextRow = alertStatusHistory.getRow(i+1);
        var nextRowStatus = (nextRow ? nextRow.watcherStatus : '');

        if (row.watcherStatus == 'INITIALIZED')
        {
            if (nextRow && (nextRow.watcherStatus == 'CALIBRATING'))
            {
                row.watcherStatus = 'CALIBRATING';
                nextRow.watcherStatus = 'INITIALIZED';
            }
        }

        var newEntry = {};
        newEntry.timestamp = row.timestamp;
        newEntry.state = row.watcherStatus;
        newEntry[row.watcherStatus+'Value'] = 0;
        if (i === 0)
        {
            if (currentAlertStatus !== 'N/A')
            {
                newEntry.timestamp = (new Date()).getTime();
                result.AddRow(newEntry);
            }
            var newEntry = {};
            newEntry.timestamp = row.timestamp;
            newEntry.state = row.watcherStatus;
            newEntry[row.watcherStatus+'Value'] = 0;
        }
        else if (i === (totalRows-1))
        {
            if (newEntry.state !== 'INITIALIZED')
            {
		        result.AddRow(newEntry);
                var newEntry = {};
                newEntry.timestamp = queryParameters.startDate.getTime();
                newEntry.state = row.watcherStatus;
                newEntry[row.watcherStatus+'Value'] = 0;
            }
        }
        if (nextRow && (row.watcherStatus !== nextRow.watcherStatus))
        {
	        result.AddRow(newEntry);
            var newEntry = {};
            newEntry.timestamp = dateAddMilliseconds(row.timestamp, -1);
            var state = getStatus(i+1, alertStatusHistory, nextRow.watcherStatus);
            newEntry.state = state + ' DONE';
            newEntry[state+'Value']=0;
        }
        result.AddRow(newEntry);
        lastTimestamp = newEntry.timestamp;
    }

    // Grab the most recent state before the window
    queryParameters.startDate = undefined;
    queryParameters.endDate = startDate;
    queryParameters.maxItems = 1;
