var result = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
    infoTableName : "InfoTable",
    dataShapeName : "AnomalyAlertState"
});

var thing = Things[thingName];

if (thing)
{
    var history = me.QueryRawAlertStreamByDate({
        thingName   : thingName,
        alertName   : alertName,
        startDate   : startDate,
        propertyName: propertyName
	});

    history = Resources["InfoTableFunctions"].Sort({
        t:          history,
        ascending:  true,
        sortColumn: 'timestamp'
    });

    var newEntry = {};
    var row;
    var previousEvent="";
    var totalRows = history.getRowCount();
    for (var i=0; history && (i < totalRows); i++)
    {
        row = history.getRow(i);

        if (((row.eventName === 'Alert') || (row.eventName === 'AlertReset')) && (previousEvent !== row.eventName))
        {
            if ((i === 0) && (row.timestamp > startDate)) // If first point is in window, extend to startDate
            {
                newEntry = {};
                newEntry.id = row.id;
                newEntry.eventName = "AlertReset Start Extended to startDate";
                newEntry.value = 0;
                newEntry.timestamp = startDate;
                result.AddRow(newEntry);

                if (row.eventName === 'Alert')
                {
                    newEntry = {};
                    newEntry.id = row.id;
                    newEntry.eventName = "AlertReset Stopped";
                    newEntry.value = 0;
                    newEntry.timestamp = dateAddMilliseconds(row.timestamp,-1);
                    result.AddRow(newEntry);
                }
            }

            if (i > 0) // Create stopping point to get nice right angle look on graph
            {
                var previousRow = result.getRow(result.getRowCount()-1);
                newEntry = {};
                newEntry.id = previousRow.id;
                newEntry.eventName = ((previousRow.eventName.indexOf('Alert ') === 0) ? "Alert" : "AlertReset") + " Stopped";
                newEntry.value = previousRow.value;
                newEntry.timestamp = dateAddMilliseconds(row.timestamp,-1);
                result.AddRow(newEntry);
            }

            newEntry = {};
            newEntry.id = row.id;
            newEntry.eventName = row.eventName + " Starts";
            newEntry.value = (row.eventName === 'Alert') ? 1 : 0;
            newEntry.timestamp = row.timestamp;
            result.AddRow(newEntry);

            previousEvent = row.eventName;
        }
    }

    if (totalRows === 0)
    {
        newEntry = {};
        newEntry.eventName = 'No Alert Data';
        newEntry.value = 0;
        newEntry.timestamp = startDate;
        result.AddRow(newEntry);

        newEntry = {};
        newEntry.eventName = 'No Alert Data';
        newEntry.value = 0;
        newEntry.timestamp = new Date();
        result.AddRow(newEntry);
    }
    else
    {
        newEntry = {};
        newEntry.id = row.id;
        newEntry.eventName=row.eventName + " Continues";
        newEntry.value = (row.eventName === 'Alert') ? 1 : 0;
        newEntry.timestamp = new Date();
        result.AddRow(newEntry);
    }

    result = Resources["InfoTableFunctions"].Sort({
        t:          result,
        ascending:  false,
        sortColumn: 'timestamp'
    });
}

