var getLastAlertEntryDateTime = function(thing, propertyName, alertName)
{
    var row;
    var queryString = '{"filters": {"type": "AND", "filters": [ ';
    queryString += '{"fieldName": "sourceProperty", "type": "EQ", "value": "' + propertyName + '" }';
    queryString += ',{"fieldName": "name", "type": "EQ", "value": "' + alertName + '" }';
    queryString += ' ] } }';
    var states = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
        infoTableName: "InfoTable",
        dataShapeName: "AnomalyAlertState"
    });

	var queryParameters = {
        query   : queryString,
        maxItems: 1
    };

    var history = thing.QueryAlertHistory(queryParameters);
    if (history && history.getRowCount() > 0)
        row = history.getRow(0);

    return row;
};

