    try
    {
        var alertDefinition = thing.SetNumberAlertParameter({property: propertyName, alertName: alertName, parameterName: 'certainty', value: certainty, persistent: true});
    }
    catch (err)
    {
        var str = "SetAnomalyCertainty: Failed to set certainty for alert '" + alertName + "' on property '" + propertyName + "' on thing '" + thingName + '": Error: '+ err;
        logger.error(str);
        throw str;
    }

    try
    {
	    thing.RestartThing();
    }
    catch (err)
    {
        var str = "SetAnomalyCertainty: Failed to restart thing '" + thingName + '": Error: '+ err;
        logger.error(str);
        throw str;
    }
}
else
{
    var str = "GetAnomalyCertainty: thing '" +thingName + "' does NOT exist";
    logger.error(str);
    throw str;
}
